import React from 'react';
import PropTypes from 'prop-types';
import CourseCard from './CourseCard';
import CircularProgressBar from './CircularProgressBar';
import { Link } from 'react-router-dom';

const ViewCourses = ({ courses }) => {
  // Calculate progress counts by status
  const newCount = courses.filter(course => course.status === 'New').length;
  const inProgressCount = courses.filter(course => course.status === 'In-Progress').length;
  const completedCount = courses.filter(course => course.status === 'Completed').length;
  const overdueCount = courses.filter(course => course.status === 'Overdue').length;

  // Calculate progress percentages by status
  const totalCount = courses.length;
  const newPercentage = (newCount / totalCount) * 100;
  const inProgressPercentage = (inProgressCount / totalCount) * 100;
  const completedPercentage = (completedCount / totalCount) * 100;
  const overduePercentage = (overdueCount / totalCount) * 100;

  return (
    <div>
     <nav>
        <ul>
          <li>
            <a href="/homepage">Home</a>
          </li>
          <li>
            <Link to="/guidelines"><a href="/guidelines">Guidelines</a></Link>
          </li>
          <li>
            <a href="/download-offer-letter">Download Offer Letter</a>
          </li>
          <li>
            <a href="/upload-documents">Upload Documents</a>
          </li>
          <li>
            <Link to="view-courses"><a href="/view-courses">View Courses</a></Link>
          </li>
          <li>
            <a href="/tasks">Tasks</a>
          </li>
          <li>
            <a href="/raise-issue">Raise Issue</a>
          </li>
        </ul>
      </nav>
    <div>
      <div className="circular-progress-container">
        <div className="circular-progress-item">
          <h3>New/(Not Started)</h3>
          <CircularProgressBar percentage={newPercentage} />
          <p>{newCount} courses</p>
        </div>
        <div className="circular-progress-item">
          <h3>In-Progress</h3>
          <CircularProgressBar percentage={inProgressPercentage} />
          <p>{inProgressCount} courses</p>
        </div>
        <div className="circular-progress-item">
          <h3>Completed</h3>
          <CircularProgressBar percentage={completedPercentage} />
          <p>{completedCount} courses</p>
        </div>
        <div className="circular-progress-item">
          <h3>Overdue</h3>
          <CircularProgressBar percentage={overduePercentage} />
          <p>{overdueCount} courses</p>
        </div>
      </div>

      <div className="course-card-container">
        {courses.slice(0, 10).map(course => (
          <CourseCard
            key={course.id}
            name={course.name}
            progress={course.progress}
            lastDate={course.lastDate}
          />
        ))}
      </div>
    </div>
    </div>
  );
};

ViewCourses.propTypes = {
  courses: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      name: PropTypes.string.isRequired,
      progress: PropTypes.number.isRequired,
      lastDate: PropTypes.string.isRequired,
      status: PropTypes.oneOf(['New', 'In-Progress', 'Completed', 'Overdue']).isRequired,
    })
  ).isRequired,
};

export default ViewCourses;
