import React, { useState } from 'react';

function MentorSignInPage() {
  const [formData, setFormData] = useState({ username: '', password: '' });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (formData.username && formData.password) {
      // Here you could add your own logic to handle form submission,
      // such as sending a request to a server or validating credentials.
      console.log(`Submitting form with username ${formData.username} and password ${formData.password}`);
    } else {
      alert('Please fill all required fields');
    }
  };

  return (
    <center>
    <div className="mentor-sign-in-page" style={{backgroundColor:'grey'}}> <br></br><br></br>
      <h1>Sign in as Mentor</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
            placeholder="Enter your Employee ID"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Submit</button>
      </form><br></br><br></br><br></br><br></br>
    </div>
    </center>
  );
}

export default MentorSignInPage;
