import React, { useState } from 'react';
import TraineeDetailsPage from './TraineeDetailsPage';

function MentorHomePage() {
  const [trainees, setTrainees] = useState([{ id: 1, name: "Alice" },
  { id: 2, name: "Bob" },
  { id: 3, name: "Charlie" },]); // array of trainee objects
  const [currentPage, setCurrentPage] = useState(1);
  const [traineePerPage] = useState(10);
  const [selectedTrainee, setSelectedTrainee] = useState(null);

  // Calculate index of the last trainee in the current page
  const lastIndex = currentPage * traineePerPage;

  // Calculate index of the first trainee in the current page
  const firstIndex = lastIndex - traineePerPage;

  // Get the trainees for the current page
  const currentTrainees = trainees.slice(firstIndex, lastIndex);

  // Calculate the total number of pages
  const totalPages = Math.ceil(trainees.length / traineePerPage);

  // Handle page change
  const handlePageChange = (page) => {
    setCurrentPage(page);
    setSelectedTrainee(null); // Reset selected trainee when changing page
  };

  // Handle trainee click
  const handleTraineeClick = (trainee) => {
    setSelectedTrainee(trainee);
  };

  return (
    <div>
      <h1>Trainees Info</h1>
      {selectedTrainee ? (
        <TraineeDetailsPage trainee={selectedTrainee} />
      ) : (
        <ul>
          {currentTrainees.map((trainee) => (
            <li key={trainee.id} onClick={() => handleTraineeClick(trainee)}>
              {trainee.name}
            </li>
          ))}
        </ul>
      )}
      <div>
        {Array.from({ length: totalPages }).map((_, index) => (
          <button key={index} onClick={() => handlePageChange(index + 1)}>
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
}

export default MentorHomePage;
