import React, { useState } from "react";
import "./UploadDocumentsPage.css";

const FileUploader = () => {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile.type === "application/pdf" || selectedFile.type === "image/jpeg") {
      setFile(selectedFile);
    } else {
      alert("Only PDF and JPG files are allowed!");
    }
  };

  const handleSubmit = () => {
    // You can perform an action here with the uploaded file
    console.log(file);
  };

  return (
    <div className="file-uploader">
      <h2 className="title">Upload Documents</h2>
      <div className="file-input">
        <label htmlFor="degree-certificate">Upload Degree Certificate:</label>
        <input
          type="file"
          id="degree-certificate"
          name="degree-certificate"
          accept=".pdf,.jpg"
          onChange={handleFileChange}
        />
      </div>
      <div className="file-input">
        <label htmlFor="aadhaar-card">Upload Aadhaar Card:</label>
        <input
          type="file"
          id="aadhaar-card"
          name="aadhaar-card"
          accept=".pdf,.jpg"
          onChange={handleFileChange}
        />
      </div>
      <div className="file-input">
        <label htmlFor="pan-card">Upload Pan Card:</label>
        <input
          type="file"
          id="pan-card"
          name="pan-card"
          accept=".pdf,.jpg"
          onChange={handleFileChange}
        />
      </div>
      <button className="submit-btn" onClick={handleSubmit} disabled={!file}>
        Submit
      </button>
    </div>
  );
};

export default FileUploader;
