import React, { useState } from 'react';
import { Link } from 'react-router-dom';


function RaiseIssue() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [faqs, setFaqs] = useState([
    { question: 'How do I reset my password?', answer: 'You can reset your password by clicking the "forgot password" link on the login page.' },
    { question: 'How do I delete my account?', answer: 'You can delete your account by navigating to the account settings page and clicking the "delete account" button.' },
    { question: 'What should I do if I encounter a bug?', answer: 'You can report a bug by filling out the "Raise an Issue" form on the support page.' }
  ]);
  const [expandedFaqs, setExpandedFaqs] = useState(Array(faqs.length).fill(false));

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  }

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  }

  const handleToggleFaq = (index) => {
    const newExpandedFaqs = [...expandedFaqs];
    newExpandedFaqs[index] = !newExpandedFaqs[index];
    setExpandedFaqs(newExpandedFaqs);
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    // Do something with the title and description
    // For example, send it to a server using an API call
    setTitle('');
    setDescription('');
  }

  return (
    <div>
    <nav>
        <ul>
          <li>
            <a href="/homepage">Home</a>
          </li>
          <li>
            <Link to="/guidelines">
              <a href="/guidelines">Guidelines</a>
            </Link>
          </li>
          <li>
            <a href="/download-offer-letter">Download Offer Letter</a>
          </li>
          <li>
            <a href="/upload-documents">Upload Documents</a>
          </li>
          <li>
            <a href="/view-courses">View Courses</a>
          </li>
          <li>
            <a href="/tasks">Tasks</a>
          </li>
          <li>
            <a href="/raise-issue">Raise Issue</a>
          </li>
        </ul>
      </nav>
      <div className="raise-issue-container">
      <h1 className="raise-issue-title">Raise an Issue</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input id="title" className="form-control" type="text" value={title} onChange={handleTitleChange} />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea id="description" className="form-control" value={description} onChange={handleDescriptionChange} />
        </div>
        <button type="submit" className="submit-button">Submit</button>
      </form>
      <h2 className="faq-title">FAQ</h2>
      <ul className="faq-list">
        {faqs.map((faq, index) => (
          <li key={index} className="faq-item">
            <button onClick={() => handleToggleFaq(index)} className="faq-question">{faq.question}</button>
            {expandedFaqs[index] && <p className="faq-answer">{faq.answer}</p>}
          </li>
        ))}
      </ul>
    </div>
    </div>
  );
}

export default RaiseIssue;
