import React, { useState } from 'react';
import './SignInPage.css';

function SignInPage() {
  const [selectedOption, setSelectedOption] = useState('');
  const [formValues, setFormValues] = useState({
    username: '',
    password: '',
  });

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleInputChange = (event) => {
    setFormValues({
      ...formValues,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Do something with the formValues
  };

  return (
    <div className="signin-container">
      <h1 className="signin-heading">Sign In</h1>
      <form className="signin-form" onSubmit={handleSubmit}>
        <label className="signin-form-label">
          Sign in as:
          <select
            className="signin-form-select"
            value={selectedOption}
            onChange={handleOptionChange}
          >
            <option value="">--Select Option--</option>
            <option value="mentor">Sign in as Mentor</option>
            <option value="trainee">Sign in as Trainee</option>
          </select>
        </label>

        {selectedOption === 'trainee' && (
          <div className="signin-form-fields">
            <label className="signin-form-label">
              Username:
              <input
                className="signin-form-input"
                type="text"
                name="username"
                value={formValues.username}
                onChange={handleInputChange}
              />
            </label>

            <label className="signin-form-label">
              Password:
              <input
                className="signin-form-input"
                type="password"
                name="password"
                value={formValues.password}
                onChange={handleInputChange}
              />
            </label>

            <button className="signin-form-button" type="submit">
              Sign In
            </button>
          </div>
        )}
      </form>
    </div>
  );
}

export default SignInPage;
