import React from 'react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

const coursesData = [  {    title: 'Course 1',    progress: 75,    lastDate: '17/02/2023',  },  {    title: 'Course 2',    progress: 30,    lastDate: '25/02/2023',  },  {    title: 'Course 3',    progress: 100,    lastDate: '12/02/2023',  },  {    title: 'Course 4',    progress: 50,    lastDate: '20/02/2023',  },  {    title: 'Course 5',    progress: 0,    lastDate: '22/02/2023',  },  {    title: 'Course 6',    progress: 90,    lastDate: '15/02/2023',  },  {    title: 'Course 7',    progress: 10,    lastDate: '18/02/2023',  },  {    title: 'Course 8',    progress: 80,    lastDate: '10/02/2023',  },  {    title: 'Course 9',    progress: 55,    lastDate: '23/02/2023',  },  {    title: 'Course 10',    progress: 35,    lastDate: '27/02/2023',  },];

const ViewCourses = () => {
  const newCoursesCount = coursesData.filter((course) => course.progress === 0).length;
  const inProgressCoursesCount = coursesData.filter((course) => course.progress > 0 && course.progress < 100).length;
  const completedCoursesCount = coursesData.filter((course) => course.progress === 100).length;
  const overdueCoursesCount = coursesData.filter((course) => course.progress > 0 && course.progress < 100 && new Date(course.lastDate) < new Date()).length;

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <div style={{ width: '25%', textAlign: 'center' }}>
          <CircularProgressbar
            value={completedCoursesCount}
            maxValue={coursesData.length}
            text={`${completedCoursesCount}/${coursesData.length}`}
            styles={buildStyles({
              textColor: '#4857f1',
              pathColor: '#4857f1',
              trailColor: '#d6d6d6',
            })}
          />
          <div>Completed</div>
        </div>
        <div style={{ width: '25%', textAlign: 'center' }}>
          <CircularProgressbar
            value={inProgressCoursesCount}
            maxValue={coursesData.length}
            text={`${inProgressCoursesCount}/${coursesData.length}`}
            styles={buildStyles({
              textColor: '#4bb7d8',
              pathColor: '#4bb7d8',
              trailColor: '#d6d6d6',
            })}
          />
          <div>In-Progress</div>
        </div>
        <div style={{ width: '25%', textAlign: 'center' }}>
          <CircularProgressbar
            value={newCoursesCount}
            maxValue={coursesData.length}
            text={`${newCoursesCount}/${coursesData.length}`}
            styles={buildStyles({
              textColor: '#edca6',
              pathColor: '#edca6c',
              trailColor: '#d6d6d6',
            })}
            />
            <div>New/(Not Started)</div>
          </div>
          <div style={{ width: '25%', textAlign: 'center' }}>
            <CircularProgressbar
              value={overdueCoursesCount}
              maxValue={coursesData.length}
              text={`${overdueCoursesCount}/${coursesData.length}`}
              styles={buildStyles({
                textColor: '#f65c78',
                pathColor: '#f65c78',
                trailColor: '#d6d6d6',
              })}
            />
            <div>Overdue</div>
          </div>
        </div>
        <div style={{ display: 'flex', flexWrap: 'wrap', marginTop: '20px' }}>
          {coursesData.map((course) => (
            <div style={{ width: '30%', marginRight: '3%', marginBottom: '20px' }}>
              <div style={{ border: '1px solid #d6d6d6', padding: '10px', borderRadius: '5px' }}>
                <div style={{ fontWeight: 'bold', marginBottom: '10px' }}>{course.title}</div>
                <div style={{ marginBottom: '10px' }}>
                  <CircularProgressbar
                    value={course.progress}
                    text={`${course.progress}%`}
                    styles={buildStyles({
                      textColor: '#4857f1',
                      pathColor: '#4857f1',
                      trailColor: '#d6d6d6',
                    })}
                  />
                </div>
                <div>Last Date: {course.lastDate}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };
  
  export default ViewCourses;
