import React, { useState } from "react";
import { Link } from 'react-router-dom';
import './UploadDocumentsPage.css';

const FileUploader = () => {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile.type === "application/pdf" || selectedFile.type === "image/jpeg") {
      setFile(selectedFile);
    } else {
      alert("Only PDF and JPG files are allowed!");
    }
  };

  const handleSubmit = () => {
    // You can perform an action here with the uploaded file
    console.log(file);
  };

  return (
    <div>
    <nav>
        <ul>
          <li>
            <a href="/homepage">Home</a>
          </li>
          <li>
            <Link to="/guidelines"><a href="/guidelines">Guidelines</a></Link>
          </li>
          <li>
            <a href="/download-offer-letter">Download Offer Letter</a>
          </li>
          <li>
            <a href="/upload-documents">Upload Documents</a>
          </li>
          <li>
            <a href="/view-courses">View Courses</a>
          </li>
          <li>
            <a href="/tasks">Tasks</a>
          </li>
          <li>
            <a href="/raise-issue">Raise Issue</a>
          </li>
        </ul>
      </nav>
    <div className="upload-documents">
      <h2>Upload Documents</h2>
      <label htmlFor="degree-certificate">Upload Degree Certificate:</label>
      <input
        type="file"
        id="degree-certificate"
        name="degree-certificate"
        accept=".pdf,.jpg"
        onChange={handleFileChange}
      />
      <br />
      <label htmlFor="aadhaar-card">Upload Aadhaar Card:</label>
      <input
        type="file"
        id="aadhaar-card"
        name="aadhaar-card"
        accept=".pdf,.jpg"
        onChange={handleFileChange}
      />
      <br />
      <label htmlFor="pan-card">Upload Pan Card:</label>
      <input
        type="file"
        id="pan-card"
        name="pan-card"
        accept=".pdf,.jpg"
        onChange={handleFileChange}
      />
      <br />
      <button onClick={handleSubmit} disabled={!file}>
        Submit
      </button>
    </div>
    </div>
  );
};

export default FileUploader;
