import React, { useState } from 'react';
// import './SignInPage.css';
import './RegisterPage.css';
import { Link } from 'react-router-dom';

function SignInPage() {
  const [selectedOption, setSelectedOption] = useState('');
  const [formValues, setFormValues] = useState({
    username: '',
    password: '',
  });

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleInputChange = (event) => {
    setFormValues({
      ...formValues,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Do something with the formValues
  };

  return (
    <div className="form-wrapper">
      {/* <h1>Sign In</h1> */}
      <h1>Employee Onboarding Portal</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Sign in as:
          <select value={selectedOption} onChange={handleOptionChange}>
            <option value="">--Select Option--</option>
            <option value="mentor">Sign in as Mentor</option>
            <option value="trainee">Sign in as Trainee</option>
          </select>
        </label>

        {selectedOption === 'trainee' && (
          <div>
            <label>
              Username:
              <input
                type="text"
                name="username"
                value={formValues.username}
                onChange={handleInputChange}
              />
            </label>

            <label>
              Password:
              <input
                type="password"
                name="password"
                value={formValues.password}
                onChange={handleInputChange}
              />
            </label>

            <Link to="/homepage"><button type="submit">Sign In</button></Link>
            
          </div>
        )}
      </form>
    </div>
  );
}

export default SignInPage;
