import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useHistory } from 'react-router-dom';

import './RegisterPage.css';

function RegisterPage() {
  const history = useHistory(); // Add this line

  const [mentorForm, setMentorForm] = useState({
    fullName: '',
    email: '',
    contactNumber: '',
    employeeId: '',
    dob: '',
    password: '',
    confirmPassword: '',
  });

  const [traineeForm, setTraineeForm] = useState({
    fullName: '',
    email: '',
    contactNumber: '',
    candidateId: '',
    dob: '',
    password: '',
    confirmPassword: '',
  });

  const handleMentorFormChange = (event) => {
    const { name, value } = event.target;
    setMentorForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handleTraineeFormChange = (event) => {
    const { name, value } = event.target;
    setTraineeForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handleMentorFormSubmit = (event) => {
    event.preventDefault();
    // TODO: handle form submission
    history.push('/mentor-sign-in-page'); // Add this line
  };

  const handleTraineeFormSubmit = (event) => {
    event.preventDefault();
    // TODO: handle form submission
    history.push('/trainee-sign-in-page'); // Add this line
  };

  return (
    <div style={{backgroundColor:"grey"}}>
      <h1>Employee Onboarding Portal</h1>
      <div style={{ display: 'flex' }}>
        <form onSubmit={handleMentorFormSubmit}>
          <h2>Register as Mentor</h2>
          <label>
            Full Name:
            <input type="text" name="fullName" value={mentorForm.fullName} onChange={handleMentorFormChange} required />
          </label>
          <br />
          <label>
            Enterprise Email Id:
            <input type="email" name="email" value={mentorForm.email} onChange={handleMentorFormChange} required />
          </label>
          <br />
          <label>
            Contact Number:
            <input type="tel" name="contactNumber" value={mentorForm.contactNumber} onChange={handleMentorFormChange} required />
          </label>
          <br />
          <label>
            Employee Id:
            <input type="text" name="employeeId" value={mentorForm.employeeId} onChange={handleMentorFormChange} required />
          </label>
          <br />
          <label>
            Date of Birth:
            <input type="date" name="dob" value={mentorForm.dob} onChange={handleMentorFormChange} required />
          </label>
          <br />
          <label>
            Create Password:
            <input type="password" name="password" value={mentorForm.password} onChange={handleMentorFormChange} required />
            </label>
            <br />
            <label>
            Confirm Password:
            <input type="password" name="confirmPassword" value={mentorForm.confirmPassword} onChange={handleMentorFormChange} required />
            </label>
            <br />
            <button type="submit">Register as Mentor</button>
            </form>
            <form onSubmit={handleTraineeFormSubmit}>
            <h2>Register as Trainee</h2>
            <label>
            Full Name:
            <input type="text" name="fullName" value={traineeForm.fullName} onChange={handleTraineeFormChange} required />
            </label>
            <br />
            <label>
            Enterprise Email Id:
            <input type="email" name="email" value={traineeForm.email} onChange={handleTraineeFormChange} required />
            </label>
            <br />
            <label>
            Contact Number:
            <input type="tel" name="contactNumber" value={traineeForm.contactNumber} onChange={handleTraineeFormChange} required />
            </label>
            <br />
            <label>
            Candidate Id:
            <input type="text" name="candidateId" value={traineeForm.candidateId} onChange={handleTraineeFormChange} required />
            </label>
            <br />
            <label>
            Date of Birth:
            <input type="date" name="dob" value={traineeForm.dob} onChange={handleTraineeFormChange} required />
            </label>
            <br />
            <label>
            Create Password:
            <input type="password" name="password" value={traineeForm.password} onChange={handleTraineeFormChange} required />
            </label>
            <br />
            <label>
            Confirm Password:
            <input type="password" name="confirmPassword" value={traineeForm.confirmPassword} onChange={handleTraineeFormChange} required />
            </label>
            <br />
            <button type="submit">Register as Trainee</button>
            </form>
            </div>
            <p>Already have an account? <Link to="/login">Log in</Link></p>
            </div>
            );
            }
            
            export default RegisterPage;
