import React, { useState } from 'react';
// import { Link } from 'react-router-dom';
import './RegisterPage.css';
import SignInPage from './SignInPage';

function RegisterPage() {
  const [selectedOption, setSelectedOption] = useState('');
  const [formValues, setFormValues] = useState({
    fullName: '',
    email: '',
    contactNumber: '',
    employeeId: '',
    dob: '',
    password: '',
    confirmPassword: '',
  });

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleInputChange = (event) => {
    setFormValues({
      ...formValues,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Do something with the formValues
  };

  return (
    <div className="form-wrapper">
      <h1>Employee Onboarding Portal</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Register as:
          <select value={selectedOption} onChange={handleOptionChange}>
            <option value="">--Select Option--</option>
            <option value="mentor">Register as Mentor</option>
            <option value="trainee">Register as Trainee</option>
          </select>
        </label>

        {selectedOption === 'trainee' && (
          <div>
            <label>
              Full Name:
              <input
                type="text"
                name="fullName"
                value={formValues.fullName}
                onChange={handleInputChange}
              />
            </label>

            <label>
              Personal Email ID:
              <input
                type="email"
                name="email"
                value={formValues.email}
                onChange={handleInputChange}
              />
            </label>

            <label>
              Contact Number:
              <input
                type="tel"
                name="contactNumber"
                value={formValues.contactNumber}
                onChange={handleInputChange}
              />
            </label>

            <label>
              Employee ID:
              <input
                type="text"
                name="employeeId"
                value={formValues.employeeId}
                onChange={handleInputChange}
              />
            </label>

            <label>
              Date of Birth:
              <input
                type="date"
                name="dob"
                value={formValues.dob}
                onChange={handleInputChange}
              />
            </label>

            <label>
              Create Password:
              <input
                type="password"
                name="password"
                value={formValues.password}
                onChange={handleInputChange}
              />
            </label>

            <label>
              Confirm Password:
              <input
                type="password"
                name="confirmPassword"
                value={formValues.confirmPassword}
                onChange={handleInputChange}
              />
            </label>

            <button type="submit">Submit</button>

            <p>Already Registered? <a href='\SignInPage.js'>Sign In</a></p>
          </div>
        )}
      </form>
    </div>
  );
}

export default RegisterPage;
